{
    "query": "What is the invoice with the highest total value?"
}

{
    "query": "What is the invoice with the smallest total value?",
    "format_instructions_dict": {
        "title": "answer",
        "type": "object",
        "properties": {
            "invoice": {
                "type": "object",
                "description": "The invoice.",
                "properties": {
                    "access_key": {
                        "type": "string",
                        "description": "The access key."
                    },
                    "issue_date": {
                        "type": "datetime",
                        "description": "The issue date."
                    },
                    "total_value": {
                        "type": "number",
                        "description": "The total value."
                    }
                },
                "required": ["access_key", "issue_date", "total_value"]
            }
        },
        "required": ["invoice"]
    }
}

{
    "query": "List all invoices in descending order by their total values",
    "format_instructions_dict": {
        "title": "answer",
        "type": "object",
        "properties": {
            "invoices": {
                "type": "list",
                "description": "The list of invoices.",
                "properties": {
                    "access_key": {
                        "type": "string",
                        "description": "The access key."
                    },
                    "issue_date": {
                        "type": "datetime",
                        "description": "The issue date."
                    },
                    "total_value": {
                        "type": "number",
                        "description": "The total value."
                    }
                },
                "required": ["access_key", "issue_date", "total_value"]
            }
        },
        "required": ["invoice"]
    }
}
